public class Main {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		Printable p=new PrinterProxy("Alice");
		System.out.println(""+p.getPrintName()+"");
		p.setPrinterName("Bob");
		System.out.println(""+p.getPrintName()+"");
		p.print("Hello,world!");

